import React from 'react'
import styled from 'styled-components'
import axios from 'axios'
import {useEffect, useState} from 'react'
import Titulo from '../Perfil/img/download.png'
import Titulo2 from '../Perfil/img/add (1).png'


const Container = styled.div`
    width: 400px;
    height: 600px;
    position: fixed;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    border: 1px solid black;
    border-radius: 5px;
    background-color: white;
    box-shadow: rgb(0 0 0 / 6%) 0px 0px 5px;
>img{
    width: 37%;
    position: absolute;
    left: 50%;
    transform: translateX(-50%)

}

`
const ContainerBotao = styled.div`
display: flex;
justify-content: space-evenly;
-webkit-box-align: center;
align-items: center;
padding: 450px 0px; 
>button{
  border-radius: 50%;
  width: 80px;
  height: 80px;
  border: 1px solid red;
  color: red;
  font-size: 50px;
  transform: scale(0.7);
  position: relative;
  overflow: hidden;
   &:hover{cursor: pointer}
}    

`
const BotaoContainer = styled.div`
   position: fixed;
    bottom: 5px;
    right: 5px;
`


function Perfil(){
  const [perfil, setPerfil] = useState({})
  const [outroperfil, setOutroPerfil] = useState(0)
  const receberPerfil = () =>{
      axios.get("https://us-central1-missao-newton.cloudfunctions.net/astroMatch/:mayra-carver/person")
      .then((res) => {
       const perfilNovo = res.data.profile
       setPerfil(perfilNovo)   
      })
      .catch((err) => {
       console.log(err.response)
      })
    }
 useEffect(() => {
  receberPerfil()
 }, [])

const perfilNovo = () => {
setOutroPerfil(outroperfil+1)
}
  return (
      <div>
    <Container>
     <img src= {Titulo}/>
     <img src= {Titulo2} className="foto"/>
       {      perfil.id?
            <p>{perfil.name}</p>
            :
            <p>Não tem perfil disponível </p>
        }
         <hr/>
    <ContainerBotao>  
    <button>✖️</button>
    <button>💚</button>
    </ContainerBotao>
    </Container>
    <BotaoContainer>
    <button>Limpar swipes e matches</button>
    </BotaoContainer>
  </div>

)
}

export default Perfil;
